import React from "react";
import Typography from "@mui/material/Typography";
import {
  Box,
  Button,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  Grid,
  Card,
} from "@mui/material";

const RegisterStudentForm = (props) => {
  const handleUpdate = props.handleUpdate;
  const courses = props.courses;
  const schoolYears = props.schoolYears;
  const course = props.course;
  const setCourse = props.setCourse;
  const setId = props.setId;

  const handleChangeId = (event) => {
    const { value } = event.target;
    setId(value);
  };

  const handleChangeCourse = (event) => {
    const { name, value } = event.target;
    setCourse({ ...course, [name]: value });
  };

  const handleUpdateInternal = (e) => {
    e.preventDefault();
    handleUpdate(course, setCourse);
  };

  return (
    <>
      <form onSubmit={handleUpdateInternal}>
        <Box sx={{ width: "100%" }}>
          <div className="mt-4 container row row-cols-2">
            <div className="col container mt-4">
              <Grid>
                <Card
                  sx={{
                    borderRadius: "20px",
                    width: "100%",
                    p: 3,
                    boxShadow: "1px 1px 5px #333",
                  }}
                >
                  <Typography variant="h4" gutterBottom component="div">
                    Cursos
                  </Typography>
                  <br />
                  <br />

                  <FormControl fullWidth>
                    <InputLabel id="labelCourses">Cursos</InputLabel>
                    <Select
                      fullWidth
                      labelId="labelCourses"
                      name="idCourse"
                      id="idCourse"
                      label="Cursos"
                      onChange={handleChangeId}
                    >
                      <MenuItem disabled selected>
                        Seleccione un Curso
                      </MenuItem>
                      {courses.map((item) => (
                        <MenuItem value={item._id}>
                          {item.number} {item.parallel}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                </Card>
              </Grid>
            </div>
            <div className="col container mt-4">
              <Grid>
                <Card
                  sx={{
                    borderRadius: "20px",
                    width: "100%",
                    p: 3,
                    boxShadow: "1px 1px 5px #333",
                  }}
                >
                  <Typography variant="h4" gutterBottom component="div">
                    Año lectivo
                  </Typography>
                  <br />
                  <br />

                  <FormControl fullWidth>
                    <InputLabel id="labelSchoolYear">Año lectivo</InputLabel>
                    <Select
                      fullWidth
                      labelId="labelSchoolYear"
                      name="idSchoolYear"
                      id="idSchoolYear"
                      label="Año lectivo"
                      onChange={handleChangeCourse}
                    >
                      <MenuItem disabled selected>
                        Seleccione un Año Lectivo
                      </MenuItem>
                      {schoolYears.map((item) => (
                        <MenuItem value={item._id}>{item.name}</MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                </Card>
              </Grid>
            </div>
          </div>
        </Box>
        <br />
        <br />
        <Button
          variant="contained"
          size="large"
          type="submit"
          sx={{
            boxShadow: "1px 1px 5px #333",
            background: "linear-gradient(to right, #0069c0, #0069c0)",
            width: "15%",
          }}
        >
          Asignar
        </Button>
        <br />
      </form>
    </>
  );
};
export default RegisterStudentForm;
